import React, { useState } from 'react';
import { useSpring, animated } from 'react-spring';

const ExpandingSpan = () => {
  const [isHovered, setIsHovered] = useState(false);

  // Define the styles for the animated span
  const SpanAnimation = useSpring({
    height: isHovered ? '200%' : '100%', // 125% for expansion
  });

  return (
    <span
      className="expanding-span"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <animated.span className="content" style={SpanAnimation}>
        Hover me!
      </animated.span>
    </span>
  );
};

export default ExpandingSpan;
